 243	 matchesAllFilters(project) {
 244	 // Búsqueda general (ID, contrato, cliente)
 245	 if (this.currentFilters.search) {
 246	 const searchTerm = this.currentFilters.search.toLowerCase();
 247	 const searchMatch = 
 248	 (project.id && project.id.toString().toLowerCase().includes(searchTerm)) ||
 249	 (project.contrato && project.contrato.toLowerCase().includes(searchTerm)) ||
 250	 (project.cliente && project.cliente.toLowerCase().includes(searchTerm));
 251	 
 252	 if (!searchMatch) return false;
 253	 }
 254	
 255	 // Filtro por ID específico
 256	 if (this.currentFilters.id && project.id) {
 257	 if (!project.id.toString().toLowerCase().includes(this.currentFilters.id.toLowerCase())) {
 258	 return false;
 259	 }
 260	 }
 261	
 262	 // Filtro por contrato
 263	 if (this.currentFilters.contrato && project.contrato) {
 264	 if (!project.contrato.toLowerCase().includes(this.currentFilters.contrato.toLowerCase())) {
 265	 return false;
 266	 }
 267	 }
 268	
 269	 // Filtro por cliente
 270	 if (this.currentFilters.cliente && project.cliente) {
 271	 if (!project.cliente.toLowerCase().includes(this.currentFilters.cliente.toLowerCase())) {
 272	 return false;
 273	 }
 274	 }
 275	
 276	 // Filtro por estado
 277	 if (this.currentFilters.estado && project.estado !== this.currentFilters.estado) {
 278	 return false;
 279	 }
 280	
