 282	 if (this.currentFilters.region && project.region !== this.currentFilters.region) {
 283	 return false;
 284	 }
 285	
 286	 // Filtro por ciudad
 287	 if (this.currentFilters.ciudad && project.ciudad) {
 288	 if (!project.ciudad.toLowerCase().includes(this.currentFilters.ciudad.toLowerCase())) {
 289	 return false;
 290	 }
 291	 }
 292	
 293	 // Filtros de fecha de inicio
 294	 if (this.currentFilters.fechaInicioDesde || this.currentFilters.fechaInicioHasta) {
