 296	 
 297	 if (this.currentFilters.fechaInicioDesde) {
 298	 const fechaDesde = new Date(this.currentFilters.fechaInicioDesde);
 299	 if (!fechaInicio || fechaInicio < fechaDesde) {
 300	 return false;
 301	 }
 302	 }
