 241	 
 242	 if (this.currentFilters.fechaInicioDesde) {
 243	 const fechaDesde = new Date(this.currentFilters.fechaInicioDesde);
 244	 if (!fechaInicio || fechaInicio < fechaDesde) {
 245	 return false;
 246	 }
 247	 }
 248	 
 249	 if (this.currentFilters.fechaInicioHasta) {
 250	 const fechaHasta = new Date(this.currentFilters.fechaInicioHasta);
 251	 if (!fechaInicio || fechaInicio > fechaHasta) {
 252	 return false;
 253	 }
 254	 }
 255	 }
...
