 854	
 855	 applyFilters() {
 856	 this.filteredRecords = this.allRecords.filter(record => {
 857	 // Búsqueda general
 858	 if (this.currentFilters.search) {
 859	 const searchTerm = this.currentFilters.search.toLowerCase();
 860	 const searchMatch =
 861	 (record.id && record.id.toString().toLowerCase().includes(searchTerm)) ||
 862	 (record.contrato && record.contrato.toLowerCase().includes(searchTerm)) ||
 863	 (record.cliente && record.cliente.toLowerCase().includes(searchTerm));
 864	
 865	 if (!searchMatch) return false;
 866	 }
 867	
 868	 // Filtro por región
 869	 if (this.currentFilters.region && record.region !== this.currentFilters.region) {
 870	 return false;
 871	 }
 872	
 873	 // Filtro por estado
 874	 if (this.currentFilters.estado && record.estado !== this.currentFilters.estado) {
 875	 return false;
 876	 }
 877	
 878	 // Filtro por tipo de obra
 879	 if (this.currentFilters.tipoObra && record.tipo_obra_lista !== this.currentFilters.tipoObra) {
 880	 return false;
 881	 }
 882	
 883	 return true;
 884	 });
 885	
 886	 this.displayFilteredRecords();
 887	 }
 888	
 889	 displayFilteredRecords() {
 890	 const tableBody = document.getElementById('manageTableBody');
 891	 if (!tableBody) return;
 892	
 893	 tableBody.innerHTML = '';
 894	
 895	 if (this.filteredRecords.length === 0) {
 896	 const row = document.createElement('tr');
 897	 row.innerHTML = `
 898	 <td colspan="32" class="text-center" style="padding: 2rem; color: #666;">
 899	 <i class="fas fa-search" style="font-size: 2rem; margin-bottom: 1rem; display: block;"></i>
 900	 No se encontraron registros con los filtros aplicados
 901	 </td>
 902	 `;
 903	 tableBody.appendChild(row);
 904	 return;
 905	 }
