 188	 matchesAllFilters(project) {
 189	 // Búsqueda general (ID, contrato, cliente)
 190	 if (this.currentFilters.search) {
 191	 const searchTerm = this.currentFilters.search.toLowerCase();
 192	 const searchMatch = 
 193	 (project.id && project.id.toString().toLowerCase().includes(searchTerm)) ||
 194	 (project.contrato && project.contrato.toLowerCase().includes(searchTerm)) ||
 195	 (project.cliente && project.cliente.toLowerCase().includes(searchTerm));
 196	 
 197	 if (!searchMatch) return false;
 198	 }
 199	
 200	 // Filtro por ID específico
 201	 if (this.currentFilters.id && project.id) {
 202	 if (!project.id.toString().toLowerCase().includes(this.currentFilters.id.toLowerCase())) {
 203	 return false;
 204	 }
 205	 }
 206	
 207	 // Filtro por contrato
 208	 if (this.currentFilters.contrato && project.contrato) {
 209	 if (!project.contrato.toLowerCase().includes(this.currentFilters.contrato.toLowerCase())) {
 210	 return false;
 211	 }
 212	 }
 213	
 214	 // Filtro por cliente
 215	 if (this.currentFilters.cliente && project.cliente) {
 216	 if (!project.cliente.toLowerCase().includes(this.currentFilters.cliente.toLowerCase())) {
 217	 return false;
 218	 }
 219	 }
 220	
 221	 // Filtro por estado
 222	 if (this.currentFilters.estado && project.estado !== this.currentFilters.estado) {
 223	 return false;
 224	 }
 225	
 226	 // Filtro por región (exacto para el selector)
 227	 if (this.currentFilters.region && project.region !== this.currentFilters.region) {
 228	 return false;
 229	 }
 230	
 231	 // Filtro por ciudad
 232	 if (this.currentFilters.ciudad && project.ciudad) {
 233	 if (!project.ciudad.toLowerCase().includes(this.currentFilters.ciudad.toLowerCase())) {
 234	 return false;
