 51	 logger.info(f" Credenciales actualizadas para usuario: {username}")
 52	
 53	 def connect(self):
 54	 """Establece la conexión con MongoDB Atlas"""
 55	 try:
 56	 logger.info("Intentando conectar a MongoDB Atlas...")
 57	
 58	 # Crear cliente con configuración optimizada
 59	 self.client = MongoClient(
 60	 self.connection_string,
 61	 serverSelectionTimeoutMS=5000, # 5 segundos timeout
 62	 connectTimeoutMS=10000, # 10 segundos para conectar
 63	 socketTimeoutMS=20000, # 20 segundos para operaciones
 64	 maxPoolSize=50, # Máximo 50 conexiones
 65	 retryWrites=True
 66	 )
 67	
 68	 # Verificar la conexión
 69	 self.client.admin.command('ping')
 70	
 71	 # Obtener la base de datos
 72	 self.db = self.client[self.database_name]
 73	
 74	 logger.info(f"✅ Conexión exitosa a MongoDB Atlas - Base de datos: {self.database_name}")
 75	 return True
 76	
 77	 except ServerSelectionTimeoutError as e:
 78	 logger.error(f"❌ Error de timeout al conectar con MongoDB Atlas: {e}")
 79	 return False
 80	 except ConnectionFailure as e:
 81	 logger.error(f"❌ Error de conexión con MongoDB Atlas: {e}")
 82	 return False
 83	 except Exception as e:
 84	 logger.error(f"❌ Error inesperado al conectar: {e}")
 85	 return False
 86	
 87	 def get_database(self):
 88	 """Retorna la instancia de la base de datos"""
 89	 if self.db is None:
 90	 if not self.connect():
 91	 raise Exception("No se pudo establecer conexión con MongoDB Atlas")
 92	 return self.db
 93	
 94	 def get_collection(self, collection_name):
 95	 """Retorna una colección específica"""
 96	 db = self.get_database()
 97	 return db[collection_name]
 98	
 99	 def test_connection(self):
 100	 """Prueba la conexión y retorna información del servidor"""
 101	 try:
 102	 if not self.client:
 103	 self.connect()
 104	
 105	 # Obtener información del servidor
 106	 server_info = self.client.server_info()
 107	 db_stats = self.db.command("dbstats")
 108	
 109	 connection_info = {
 110	 "status": "connected",
 111	 "server_version": server_info.get("version"),
 112	 "database": self.database_name,
 113	 "collections_count": len(self.db.list_collection_names()),
 114	 "db_size_mb": round(db_stats.get("dataSize", 0) / (1024 * 1024), 2)
 115	 }
 116	
 117	 logger.info(f" Información de conexión: {connection_info}")
 118	 return connection_info
 119	
 120	 except Exception as e:
 121	 logger.error(f"❌ Error al probar conexión: {e}")
 122	 return {"status": "error", "message": str(e)}
 123	
 124	 def close_connection(self):
 125	 """Cierra la conexión con MongoDB"""
 126	 if self.client:
 127	 self.client.close()
 128	 logger.info(" Conexión cerrada")
 129	
 130	# Mapeo de tipos de usuario a credenciales de MongoDB
 131	# Nota: Ambos tipos de usuario usan las mismas credenciales de MongoDB Atlas
 132	# La diferenciación de permisos se maneja a nivel de aplicación
 133	USER_CREDENTIALS = {
 134	 'admin': {'username': 'Admin', 'password': 'Admin123'},
 135	 'reader': {'username': 'Admin', 'password': 'Admin123'} # Usar las mismas credenciales válidas
 136	}
 137	
 138	# Instancia global de la conexión
 139	db_connection = DatabaseConnection()
 140	
 141	def get_db_for_user(user_type='admin'):
 142	 """Función helper para obtener la base de datos con credenciales específicas del usuario"""
 143	 if user_type in USER_CREDENTIALS:
 144	 credentials = USER_CREDENTIALS[user_type]
 145	 db_connection.update_credentials(credentials['username'], credentials['password'])
 146	 return db_connection.get_database()
 147	
 148	def get_collection_for_user(collection_name, user_type='admin'):
 149	 """Función helper para obtener una colección con credenciales específicas del usuario"""
 150	 if user_type in USER_CREDENTIALS:
 151	 credentials = USER_CREDENTIALS[user_type]
 152	 db_connection.update_credentials(credentials['username'], credentials['password'])
 153	 return db_connection.get_collection(collection_name)
 154	
 155	def get_db():
 156	 """Función helper para obtener la base de datos (mantiene compatibilidad)"""
 157	 return db_connection.get_database()
 158	
 159	def get_collection(collection_name):
 160	 """Función helper para obtener una colección (mantiene compatibilidad)"""
 161	 return db_connection.get_collection(collection_name)
