 75	
 76	 setupFilters() {
 77	 // Toggle filters visibility
 78	 const toggleFilters = document.getElementById('toggleFilters');
 79	 const filtersContainer = document.getElementById('filtersContainer');
 80	
 81	 if (toggleFilters && filtersContainer) {
 82	 toggleFilters.addEventListener('click', () => {
 83	 if (filtersContainer.style.display === 'none') {
 84	 filtersContainer.style.display = 'block';
 85	 toggleFilters.textContent = 'Ocultar';
 86	 } else {
 87	 filtersContainer.style.display = 'none';
 88	 toggleFilters.textContent = 'Mostrar';
 89	 }
 90	 });
 91	 }
 92	
 93	 // Quick search filter
 94	 const quickSearchFilter = document.getElementById('quickSearchFilter');
 95	 if (quickSearchFilter) {
 96	 quickSearchFilter.addEventListener('input', (e) => {
 97	 this.currentFilters.search = e.target.value.trim();
 98	 this.applyFilters();
 99	 });
 100	 }
 101	
 102	 // Select filters with auto-apply
 103	 const selectFilters = ['quickRegionFilter', 'quickEstadoFilter', 'quickTipoObraFilter'];
 104	 selectFilters.forEach(filterId => {
 105	 const filter = document.getElementById(filterId);
 106	 if (filter) {
 107	 filter.addEventListener('change', () => {
 108	 const filterKey = filterId.replace('quick', '').replace('Filter', '').toLowerCase();
 109	 this.currentFilters[filterKey === 'tipoobrafilter' ? 'tipoObra' : filterKey] = filter.value;
 110	 this.applyFilters();
 111	 });
 112	 }
 113	 });
 114	
 115	 // Clear filters button
 116	 const clearQuickFiltersBtn = document.getElementById('clearQuickFiltersBtn');
 117	 if (clearQuickFiltersBtn) {
 118	 clearQuickFiltersBtn.addEventListener('click', () => {
 119	 this.clearFilters();
 120	 });
 121	 }
 122	 }
