 963	 clearFilters() {
 964	 // Limpiar filtros
 965	 this.currentFilters = {
 966	 search: '',
 967	 region: '',
 968	 estado: '',
 969	 tipoObra: ''
 970	 };
 971	
 972	 // Limpiar campos de filtro
 973	 const filterInputs = ['quickSearchFilter', 'quickRegionFilter', 'quickEstadoFilter', 'quickTipoObraFilter'];
 974	 filterInputs.forEach(inputId => {
 975	 const input = document.getElementById(inputId);
 976	 if (input) {
 977	 input.value = '';
 978	 }
 979	 });
 980	
 981	 // Aplicar filtros (mostrar todos)
 982	 this.applyFilters();
 983	 UIComponents.showNotification('Filtros limpiados', 'info');
 984	 }
