 73	 self.hidraulica_hidrologia = hidraulica_hidrologia
 74	 self.descripcion = descripcion
 75	 self.certificado_experiencia = certificado_experiencia
 76	 self.orden_compra = orden_compra
 77	 self.contrato_doc = contrato_doc
 78	 self.factura = factura
 79	 self.numero_factura = numero_factura
 80	 self.numero_orden_compra = numero_orden_compra
 81	 self._id = _id # MongoDB ObjectId
 82	 self.created_at = created_at if created_at else datetime.now()
 83	 self.updated_at = updated_at if updated_at else datetime.now()
 84	
 85	 def to_dict(self) -> Dict[str, Any]:
 86	 """Convierte el objeto a diccionario para MongoDB"""
 87	 data = {
 88	 'id': self.id,
 89	 'contrato': self.contrato,
 90	 'cliente': self.cliente,
 91	 'fecha_inicio': self.fecha_inicio,
 92	 'fecha_termino': self.fecha_termino,
 93	 'region': self.region,
 94	 'ciudad': self.ciudad,
 95	 'estado': self.estado,
 96	 'monto': self.monto,
 97	 # Información del cliente
 98	 'rut_cliente': self.rut_cliente,
 99	 'tipo_cliente': self.tipo_cliente,
 100	 'persona_contacto': self.persona_contacto,
 101	 'telefono_contacto': self.telefono_contacto,
 102	 'correo_contacto': self.correo_contacto,
 103	 # Información técnica
 104	 'superficie_terreno': self.superficie_terreno,
 105	 'superficie_construida': self.superficie_construida,
 106	 'tipo_obra_lista': self.tipo_obra_lista,
 107	 # Estudios y servicios
 108	 'ems': self.ems,
 109	 'estudio_sismico': self.estudio_sismico,
 110	 'estudio_geoelectrico': self.estudio_geoelectrico,
 111	 'topografia': self.topografia,
 112	 'sondaje': self.sondaje,
 113	 'hidraulica_hidrologia': self.hidraulica_hidrologia,
 114	 'descripcion': self.descripcion,
 115	 'certificado_experiencia': self.certificado_experiencia,
 116	 'orden_compra': self.orden_compra,
 117	 'contrato_doc': self.contrato_doc,
 118	 'factura': self.factura,
 119	 'numero_factura': self.numero_factura,
 120	 'numero_orden_compra': self.numero_orden_compra,
 121	 'created_at': self.created_at,
 122	 'updated_at': datetime.now() # Siempre actualizar timestamp
 123	 }
 124	
 125	 # Solo incluir _id si existe
 126	 if self._id:
 127	 data['_id'] = self._id
 128	
 129	 return data
