 241	# Constantes para los estados disponibles
 242	STATUS_OPTIONS = [
 243	 "Activo",
 244	 "Completado",
 245	 "Pendiente"
 246	]
 247	
 248	# Mapeo de colores para estados (para uso en frontend)
 249	STATUS_COLORS = {
 250	 "Activo": "#3498db", # Azul
 251	 "Completado": "#27ae60", # Verde
 252	 "Pendiente": "#f39c12" # Naranja
 253	}
 254	
 255	# Función para validar el estado
 256	def validate_status(status: str) -> bool:
 257	 """Valida si el estado es válido"""
 258	 return status in STATUS_OPTIONS
 259	
 260	# Función para formatear fecha
 261	def format_date(date: datetime) -> str:
 262	 """Formatea la fecha para mostrar en la interfaz"""
 263	 if isinstance(date, datetime):
 264	 return date.strftime("%Y-%m-%d")
 265	 elif isinstance(date, str):
 266	 try:
 267	 # Intentar parsear si es string ISO
 268	 parsed_date = datetime.fromisoformat(date.replace('Z', '+00:00'))
 269	 return parsed_date.strftime("%Y-%m-%d")
 270	 except:
 271	 return date
 272	 return str(date) if date else ""
 273	
 274	# Función para formatear cantidad
 275	def format_amount(amount: float) -> str:
 276	 """Formatea la cantidad como moneda chilena"""
 277	 return f"${amount:,.0f} CLP"
 278	
 279	# Función para crear índices en MongoDB
 280	def create_indexes(collection):
 281	 """Crea índices optimizados para la colección de proyectos"""
 282	 try:
 283	 # Índice en el campo 'id' (no único)
 284	 collection.create_index("id")
 285	
 286	 # Índice compuesto para búsquedas frecuentes
 287	 collection.create_index([("estado", 1), ("fecha_inicio", -1)])
 288	
 289	 # Índice de texto para búsqueda por cliente y contrato
 290	 collection.create_index([("cliente", "text"), ("contrato", "text")])
 291	
 292	 # Índice en created_at para ordenamiento
 293	 collection.create_index("created_at")
 294	
 295	 logger.info("✅ Índices creados exitosamente")
 296	 return True
 297	
 298	 except Exception as e:
 299	 logger.error(f"❌ Error creando índices: {e}")
 300	 return False
 301	
 302	# Función para obtener estadísticas de la colección
 303	def get_collection_stats(collection) -> Dict[str, Any]:
 304	 """Obtiene estadísticas de la colección de proyectos"""
 305	 try:
 306	 # Contar documentos por estado
 307	 pipeline = [
 308	 {
 309	 "$group": {
 310	 "_id": "$status",
 311	 "count": {"$sum": 1},
 312	 "total_amount": {"$sum": "$amount"}
 313	 }
 314	 }
 315	 ]
 316	
 317	 status_stats = list(collection.aggregate(pipeline))
 318	
 319	 # Estadísticas generales
 320	 total_count = collection.count_documents({})
 321	 total_amount = collection.aggregate([
 322	 {"$group": {"_id": None, "total": {"$sum": "$amount"}}}
 323	 ])
 324	 total_amount = list(total_amount)
 325	 total_amount = total_amount[0]["total"] if total_amount else 0
 326	
 327	 return {
 328	 "total_projects": total_count,
 329	 "total_amount": total_amount,
 330	 "status_breakdown": {stat["_id"]: stat for stat in status_stats},
 331	 "average_amount": total_amount / total_count if total_count > 0 else 0
 332	 }
 333	
 334	 except Exception as e:
 335	 logger.error(f"❌ Error obteniendo estadísticas: {e}")
 336	 return {}
