````python path=build_system.py mode=EDIT
#!/usr/bin/env python3
"""
Sistema de construcción automático para GlaciarIng
Genera ejecutables para Windows, Mac y Linux automáticamente
"""

import os
import sys
import platform
import shutil
import subprocess
from pathlib import Path
import PyInstaller.__main__

class BuildSystem:
 def __init__(self):
 self.root_dir = Path(__file__).parent
 self.dist_dir = self.root_dir / "dist"
 self.build_dir = self.root_dir / "build"
 self.assets_dir = self.root_dir / "assets"
 self.current_os = platform.system().lower()

 def clean_build_dirs(self):
 """Limpia directorios de construcción anteriores"""
 print(" Limpiando directorios de construcción...")

 for dir_path in [self.dist_dir, self.build_dir, self.root_dir / "specs"]:
 if dir_path.exists():
 shutil.rmtree(dir_path)
 print(f" ✅ Eliminado: {dir_path}")

 # Eliminar archivos .spec
 for spec_file in self.root_dir.glob("*.spec"):
 spec_file.unlink()
 print(f" ✅ Eliminado: {spec_file}")

 def create_icon_if_missing(self):
 """Crea iconos automáticamente si no existen"""
 print(" Verificando iconos...")

 icon_dir = self.assets_dir / "images"
 icon_dir.mkdir(parents=True, exist_ok=True)

 # Crear icono básico si no existe
 icon_png = icon_dir / "icon.png"
 if not icon_png.exists():
 print(" Creando icono por defecto...")
 self.create_default_icon(icon_png)

 # Para Mac, crear .icns si no existe
 if self.current_os == "darwin":
 icon_icns = icon_dir / "icon.icns"
 if not icon_icns.exists():
 print(" Creando icono .icns para Mac...")
 self.create_icns_from_png(icon_png, icon_icns)

 def create_default_icon(self, output_path):
 """Crea un icono por defecto usando PIL"""
 try:
 from PIL import Image, ImageDraw, ImageFont

 # Crear imagen 512x512
 img = Image.new('RGBA', (512, 512), (70, 130, 180, 255))
 draw = ImageDraw.Draw(img)

 # Dibujar círculo
 draw.ellipse([50, 50, 462, 462], fill=(255, 255, 255, 255))

 # Texto "GI"
 try:
 font = ImageFont.truetype("arial.ttf", 200)
 except:
 font = ImageFont.load_default()

 draw.text((180, 180), "GI", fill=(70, 130, 180, 255), font=font)

 img.save(output_path, "PNG")
 print(f" ✅ Icono creado: {output_path}")

 except ImportError:
 print(" ⚠️ PIL no disponible, usando icono simple...")
 # Crear archivo vacío como placeholder
 output_path.touch()

 def create_icns_from_png(self, png_path, icns_path):
 """Convierte PNG a ICNS para Mac"""
 if self.current_os != "darwin":
 return

 try:
 # Crear iconset temporal
 iconset_dir = self.root_dir / "temp.iconset"
 iconset_dir.mkdir(exist_ok=True)

 # Tamaños requeridos para .icns
 sizes = [16, 32, 64, 128, 256, 512]

 for size in sizes:
 output_file = iconset_dir / f"icon_{size}x{size}.png"
 subprocess.run([
 "sips", "-z", str(size), str(size), 
 str(png_path), "--out", str(output_file)
 ], check=True, capture_output=True)

 # Crear .icns
 subprocess.run([
 "iconutil", "-c", "icns", str(iconset_dir)
 ], check=True, capture_output=True)

 # Mover resultado
 generated_icns = self.root_dir / "temp.icns"
 if generated_icns.exists():
 shutil.move(generated_icns, icns_path)
 print(f" ✅ Icono .icns creado: {icns_path}")

 # Limpiar
 shutil.rmtree(iconset_dir)

 except (subprocess.CalledProcessError, FileNotFoundError):
 print(" ⚠️ No se pudo crear .icns, usando PNG")
 shutil.copy(png_path, icns_path.with_suffix('.png'))

 def get_build_config(self):
 """Obtiene configuración de construcción según el OS"""
 base_config = [
 'api_server.py',
 '--onefile',
 '--name=GlaciarIng',
 '--distpath=dist',
 '--workpath=build',
 '--specpath=specs',
 # Incluir archivos necesarios
 '--add-data=assets:assets',
 '--add-data=controllers:controllers',
 '--add-data=models:models',
 '--add-data=db:db',
 '--add-data=templates:templates',
 # Incluir archivos HTML
 '--add-data=index.html:.',
 '--add-data=login.html:.',
 '--add-data=reader.html:.',
 '--add-data=modify-database.html:.',
 # Imports ocultos
 '--hidden-import=pymongo',
 '--hidden-import=flask',
 '--hidden-import=flask_cors',
 '--hidden-import=flask_session',
 '--hidden-import=controllers.controller',
 '--hidden-import=models.proyecto',
 '--hidden-import=db.conexion',
 '--hidden-import=bson',
 '--hidden-import=bson.objectid',
 '--hidden-import=datetime',
 '--hidden-import=logging',
 '--hidden-import=json',
 '--hidden-import=secrets',
 '--hidden-import=functools',
 # Excluir módulos innecesarios
 '--exclude-module=tkinter',
 '--exclude-module=matplotlib',
 '--exclude-module=numpy',
 ]

 # Configuración específica por OS
 if self.current_os == "windows":
 base_config.extend([
 '--icon=assets/images/icon.ico',
 '--console' # Mostrar consola en Windows para logs
 ])
 elif self.current_os == "darwin": # Mac
 icon_path = self.assets_dir / "images" / "icon.icns"
 if icon_path.exists():
 base_config.append(f'--icon={icon_path}')
 base_config.extend([
 '--windowed', # Sin consola en Mac
 '--osx-bundle-identifier=com.glaciaring.app'
 ])
 elif self.current_os == "linux":
 icon_path = self.assets_dir / "images" / "icon.png"
 if icon_path.exists():
 base_config.append(f'--icon={icon_path}')

 return base_config

 def build_executable(self):
 """Construye el ejecutable"""
 print(f" Construyendo ejecutable para {self.current_os.upper()}...")

 config = self.get_build_config()

 try:
 PyInstaller.__main__.run(config)
 print("✅ Construcción completada!")

 # Mostrar información del ejecutable
 self.show_build_info()

 except Exception as e:
 print(f"❌ Error durante la construcción: {e}")
 return False

 return True

 def show_build_info(self):
 """Muestra información del ejecutable generado"""
 dist_files = list(self.dist_dir.glob("*"))

 if dist_files:
 print("\n Ejecutables generados:")
 for file_path in dist_files:
 size_mb = file_path.stat().st_size / (1024 * 1024)
 print(f" {file_path.name} ({size_mb:.1f} MB)")
 print(f" Ubicación: {file_path.absolute()}")

 print(f"\n Para ejecutar:")
 if self.current_os == "windows":
 print(f" .\\dist\\GlaciarIng.exe")
 else:
 print(f" ./dist/GlaciarIng")
 print(f" (Asegúrate de dar permisos: chmod +x ./dist/GlaciarIng)")

